mapper.insert:
/* Mapper - "qaBoardInsert" */ INSERT INTO TUQABOARD ( QANO, QAID, QATITLE, QACONTENT, QATYPE, QAANSWERTYPE, QASECRETYN, QAIMAGE, QAREF, QASTEP, QAINSERTDATE, QAUPDATEDATE, QADELETEYN, QARECORDINSERTDATE, QARECORDUPDATEDATE, QAREPLYCONTENT ) VALUES( (SELECT NVL2(MAX(QANO),'QA'||LPAD(REPLACE(MAX(QANO),'QA')+1,4,'0'),'QA0001') FROM TUQABOARD), #{qaid}, #{qatitle}, #{qacontent}, #{qatype}, #{qaanswertype}, #{qasecretyn}, #{qaimage}, #{qaref}, #{qastep}, SYSDATE, SYSDATE, 'Y', SYSDATE, SYSDATE,
#{qareplycontent}
""
)

mapper.delete:
     /* Mapper - qaBoardDelete */ 
      UPDATE    TUQABOARD 
	    SET     QAUPDATEDATE = SYSDATE,
	    	    QADELETEYN ='N',
	            QARECORDUPDATEDATE = SYSDATE
	    WHERE   QANO = #{qano}
	    OR      QAREF= #{qaref}

mapper.update:
/* Mapper - qaBoardReplyUpdate */ 
	UPDATE  TUQABOARD 
    SET     QAREPLYCONTENT = #{qareplycontent}
    WHERE   QANO = #{qano}
    AND     QADELETEYN = 'Y'
